					t.researchTimer = setTimeout(t.researchTick, t.resRetryTime);
					if (t.contentType == 3) t.jobFeedback(translate('Completion errors') + ': ' + translate('Retry in') + ' ' + timestr(t.resRetryTime / 1000) + ' ' + translate('seconds'));
					t.resRetryTime *= 1.5;
