		function timestr(time, full) {
			time = toNum(time);
			var m = [];
			var t = time;
			if (t < 61) return t + 's';
			if (t > 86400) {
				m.push(toNum(t / 86400));
				m.push('d ');
				t %= 86400;
			}
			if (t > 3600 || time > 3600) {
				m.push(toNum(t / 3600));
				m.push('h ');
				t %= 3600;
			}
			m.push(toNum(t / 60));
			m.push('m');
			if (full || time <= 3600) {
				m.push(' ');
				m.push(t % 60);
				m.push('s');
			}
			var str = m.join('');
			if (str[str.length - 1] == ' ')
				str = str.substring(0, str.length - 1);
			return str;
		}

		function timestrShort(time) {
			time = toNum(time);
			if (time > 86400) {
				var m = [];
				time /= 3600;
				m.push(toNum(time / 24));
				m.push('d ');
				m.push(toNum(time % 24));
				m.push('h');
				return m.join('');
			} else return timestr(time);
		}

